import React ,{useContext}from 'react'
import { Products } from '../products/Products'
import Product from './Product'
import './Shop.css';
import {proContext} from "./Context"
import { useNavigate } from 'react-router-dom'



export default function Shop() {
  const{searchItem,setSearchIte}=useContext(proContext)
  const nav=useNavigate()

  console.log(searchItem)
  if(searchItem.length===0){
    return(
    <div >
        <div>“您搜索的商品不存在”</div>
        <button onClick={()=>nav("/")}>返回主页</button>

    </div>)
  } else {
  return (
    <div className='shop'>
        <div className='shopTitle'>全部商品</div>
        <div>{Products.map((product)=><Product data={product} key={product.id}/>)}</div>
    </div>
  )
}}
