import React, { useState } from 'react'
import {MagnifyingGlass} from "phosphor-react"
import {proContext} from "./Context"
import { Products } from '../products/Products'
import { useContext } from "react"
function Search() {
  const[inputInfo,setInputInfo]=useState()

const{setCartItem}=useContext(proContext)
const handleSearch=()=>{
  const searchWord=inputInfo;
  console.log(searchWord)
  const newArr=Products.filter((item)=>(item.name.toLowerCase().includes(searchWord.toLowerCase())))
  // console.log(newArr)
  return setCartItem(newArr)
}

//   const  a=[{id:"1",name:"Abc"},{id:"2",name:"efSg"},{id:"3",name:"fSgh"},{id:"4",name:"tgFj"}]
// const seacherWord="gh";
// const newa=a.filter((item)=>(item.name.toLowerCase().includes(seacherWord.toLowerCase())))
// console.log(newa);
  return (
    <div>
        <input value={inputInfo} onChange={(e)=>setInputInfo(e.target.value)} placeholder='搜索商品'/>
        <button onClick={handleSearch}><MagnifyingGlass size={20} /></button>
    </div>
  )
}

export default Search
