import React, { useContext } from 'react'
import {proContext} from "./Context"
import './Cart.css'

export default function Product(props) {
    const {id,name,price,proImage} = props.data
    const{cartItem,AddProduct,RemoveProduct,InputProduct}=useContext(proContext)
    const addOnePro=()=>{
      if(cartItem[id]!==0){
       return cartItem[id]
      } else return AddProduct(id)-1
    }

   console.log(cartItem)
  return (
    <div>
        <img src={proImage} alt="products"/>
        <p>产品名称：{name}</p>
        <p>价格：¥{price}元</p>
        <button className='addToCartBttn' onClick={()=>addOnePro()}>加入购物车</button>
        <p>
        <button onClick={()=>RemoveProduct(id)}>-</button>
        {' '}
        <input value={cartItem[id]}  onChange={(e)=>InputProduct(id,(e.target.value)*1)} style={{width:50}}/>
        {' '}
        <button onClick={()=>AddProduct(id)}>+</button>
        </p>

    </div>
  )
}
