import { useState } from 'react';
import { Link } from 'react-router-dom';
import {ShoppingOutlined, MedicineBoxOutlined ,PartitionOutlined } from '@ant-design/icons';
import Makeups from './Makeups';


const Nav = () => {
  const [Visible, setVisible] = useState(null);
  const handleDisplay=(index)=>{
    setVisible(Visible=== index ? null : index)
  }

  return (
    <div style={{width:"300"}}>
    <Link to="/makeups"><ShoppingOutlined style={{fontSize: '30px', color: 'orange' }} onClick={()=>handleDisplay(0)}/> 热销产品{Visible===0?<Makeups/>:""}</Link>
    <Link to="/makeups"><ShoppingOutlined style={{fontSize: '30px', color: 'orange' }} onClick={()=>handleDisplay(1)}/> 化妆护肤{Visible===1?<Makeups/>:""}</Link>
    <Link to="/medicine"><MedicineBoxOutlined style={{ fontSize: '30px', color: 'orange' }} onClick={()=>handleDisplay(2)}/>药品保健{Visible===2?<Makeups/>:""}</Link>
    <Link to="/baby"><MedicineBoxOutlined style={{ fontSize: '30px', color: 'orange' }} onClick={()=>handleDisplay(3)}/>母婴产品{Visible===3?<Makeups/>:""}</Link>
    <Link to="/others"><PartitionOutlined style={{ fontSize: '30px', color: 'orange' }} onClick={()=>handleDisplay(4)}/> 其他{Visible===4?<Makeups/>:""}</Link> 
    </div>
  );
}; 
export default Nav;
