import React, { useState,useContext } from 'react'
import {MagnifyingGlass} from "phosphor-react"
import { Products } from '../products/Products'
import {proContext} from "./Context"

function Search() {
  const[inputInfo,setInputInfo]=useState()
  const{SearchValue}=useContext(proContext)

 const handleSearch=()=>{

  if(inputInfo){
  const searchWord=inputInfo;
  const newArr=Products.filter((item)=>item.name.toLowerCase().includes(searchWord.toLowerCase()))
  SearchValue(newArr)
  setInputInfo("")}
}
  return (
    <div>
        <input value={inputInfo} onChange={(e)=>setInputInfo(e.target.value)} placeholder='搜索商品'/>
        <button onClick={handleSearch}><MagnifyingGlass size={20} /></button>
    </div>
  )
}

export default Search
