import React, { createContext, useState } from 'react'
import { Products } from '../products/Products';

export const proContext=createContext(null);
const defaultCart=()=>{
    let cart={};
    for(let i=1;i<Products.length+1;i++){
        cart[i]=0;
    }
    return cart;
}

export const  ContextProvider=(props)=> {
    const[cartItem,setCartItem]=useState(defaultCart())

    const AddProduct=(id)=>{
    setCartItem((prev)=>({...prev,[id]:prev[id]+1}))
    }
    const RemoveProduct=(id)=>{
        setCartItem((prev)=>({...prev,[id]:prev[id]-1}))
    }
    const ContextValue={cartItem,AddProduct,RemoveProduct}
 console.log(ContextValue)
  return (
    <proContext.Provider value={ContextValue}>{props.chilren}</proContext.Provider>
  )
}
