import React, { useState } from 'react'
import checkImg from "./Wechat.jpeg"
import { useContext } from 'react'
import {proContext} from "../Context"
export default function Checked() {
  const{TotalPrice}=useContext(proContext)
    const total = TotalPrice();
    const [postMes,setPostMes]=useState({
      postCode:"",postAddress:"",postName:"",postCall:""
    })
  return (
    <div>
      <p>微信收款码</p>
      {total>0?<div>
        <p>请填写您的收货信息：</p>
        <input value={postMes.postCode} onChange={(e)=>setPostMes(e.target.value)}>邮编：</input>
        <input value={postMes.postAddress}>地址：</input>
        <input value={postMes.postName}>收货人：</input>
        <input type="tel" pattern="[0-9]{11}" value={postMes.postCall}>电话：</input>
        <img src={checkImg} alt="checkImg" style={{width:150,height:150}}/>
        总金额：¥{total}
        </div>:<p>“您的购物车是空的”</p>}

    </div>
  )
}
