import { useState } from 'react';
import { Link } from 'react-router-dom';
import {ShoppingOutlined,
   MedicineBoxOutlined ,
   PartitionOutlined ,
   SmileOutlined} from '@ant-design/icons';
import Makeups from './Makeups';


const Nav = () => {
  const [Visible, setVisible] = useState(null);
  const handleDisplay=(index)=>{
    setVisible(Visible=== index ? null : index)
  }

  return (
    <div style={{width:"110px"}}>
    <Link to="/makeups"><ShoppingOutlined style={{fontSize: '30px', color: 'orange' }} onClick={()=>handleDisplay(0)}/> <span onClick={()=>handleDisplay(1)}>热销产品</span>{Visible===0?<Makeups/>:""}</Link>
    <></>
    <></>
    <Link to="/makeups"><ShoppingOutlined style={{fontSize: '30px', color: 'orange' }} onClick={()=>handleDisplay(1)}/> <span onClick={()=>handleDisplay(1)}>化妆护肤</span>{Visible===1?<Makeups/>:""}</Link>
    <></>
    <></>
    <Link to="/medicine"><MedicineBoxOutlined style={{ fontSize: '30px', color: 'orange' }} onClick={()=>handleDisplay(2)}/>  <span onClick={()=>handleDisplay(1)}>药品保健</span>{Visible===2?<Makeups/>:""}</Link>
    <></>
    <></>
    <Link to="/baby"><SmileOutlined style={{ fontSize: '30px', color: 'orange' }} onClick={()=>handleDisplay(3)}/> <span onClick={()=>handleDisplay(1)}>母婴产品</span>{Visible===3?<Makeups/>:""}</Link>
    <></>
    <></>
    <Link to="/others"><PartitionOutlined style={{ fontSize: '30px', color: 'orange' }} onClick={()=>handleDisplay(4)}/> <span onClick={()=>handleDisplay(1)}> 其他</span>{Visible===4?<Makeups/>:""}</Link> 
    </div>
  );
}; 
export default Nav;
