import { useState } from 'react';
import { Link } from 'react-router-dom';
import {ShoppingOutlined,
   MedicineBoxOutlined ,
   PartitionOutlined ,
   SmileOutlined} from '@ant-design/icons';
import Makeups from './Makeups';
import './Nav.css'


const Nav = () => {
  const [Visible, setVisible] = useState(null);
  const handleDisplay=(index)=>{
    setVisible(Visible=== index ? null : index)
  }

  return (
    <div className='Div'>
    <Link to="/makeups"><ShoppingOutlined className='LinkIcon' onClick={()=>handleDisplay(0)}/> <span onClick={()=>handleDisplay(0)}>热销产品</span></Link>
    <></>
    <></>
    <Link to="/makeups"><ShoppingOutlined className='LinkIcon' onClick={()=>handleDisplay(1)}/> <span onClick={()=>handleDisplay(1)}>化妆护肤</span>{Visible===1?<Makeups/>:""}</Link>
    <></>
    <></>
    <Link to="/medicine"><MedicineBoxOutlined className='LinkIcon' onClick={()=>handleDisplay(2)}/>  <span onClick={()=>handleDisplay(2)}>药品保健</span>{Visible===2?<Makeups/>:""}</Link>
    <></>
    <></>
    <Link to="/baby"><SmileOutlined className='LinkIcon' onClick={()=>handleDisplay(3)}/> <span onClick={()=>handleDisplay(3)}>母婴产品</span>{Visible===3?<Makeups/>:""}</Link>
    <></>
    <></>
    <Link to="/others"><PartitionOutlined className='LinkIcon' onClick={()=>handleDisplay(4)}/> <span onClick={()=>handleDisplay(4)}> 其他</span>{Visible===4?<Makeups/>:""}</Link> 
    </div>
  );
}; 
export default Nav;
