import React from 'react';
import {ContextProvider} from "./component/Context"
import { LaptopOutlined,
  NotificationOutlined, 
  UserOutlined,
  HomeOutlined,
  ShoppingCartOutlined,
  CreditCardOutlined} from '@ant-design/icons';
import { Breadcrumb, Layout, Menu, theme } from 'antd';
import { BrowserRouter, Routes, Route,Link } from "react-router-dom";
import Shop from './component/Shop';
import Cart from './component/Cart';
import Checked from './component/checked/Checked';

// import Nav from './component/Nav';
//import Search from './component/Search';

const { Header, Content, Footer, Sider } = Layout;
const homePage=<Link to="/"><HomeOutlined style={{ fontSize: '30px', color: 'orange' }}/>店铺首页</Link>
const shopCart= <Link to="/cart"><ShoppingCartOutlined style={{ fontSize: '30px', color: 'orange' }}/>({1})购物车</Link>
const checked= <Link to="/checked"><CreditCardOutlined style={{ fontSize: '30px', color: 'orange' }}/>结账</Link>
const items1 = [homePage, shopCart, checked].map((key) => ({
  key,
  label: `${key}`,
}));
const items2 = [UserOutlined, LaptopOutlined, NotificationOutlined].map((icon, index) => {
  const key = String(index + 1);
  return {
    key: `sub${key}`,
    icon: React.createElement(icon),
    label: `subnav ${key}`,
    children: new Array(4).fill(null).map((_, j) => {
      const subKey = index * 4 + j + 1;
      return {
        key: subKey,
        label: `option${subKey}`,
      };
    }),
  };
});
const App = () => {
  const {
    token: { colorBgContainer },
  } = theme.useToken();
  return (
    <ContextProvider>
    <BrowserRouter>
        <Routes>
          <Route path="/" element={<Shop/>}/>
          <Route path="/cart" element={<Cart/>}/>
          <Route path="/checked" element={<Checked/>}/>
        </Routes>
    <Layout>
      <Header className="header">
        <div className="logo" />
        <Menu theme="light" mode="horizontal" defaultSelectedKeys={[homePage]} items={items1} />

      </Header>
      <Content
        style={{
          padding: '0 50px',
        }}
      >
        <Breadcrumb
          style={{
            margin: '16px 0',
          }}
        >

          <Breadcrumb.Item>aa</Breadcrumb.Item>

        </Breadcrumb>
        <Layout
          style={{
            padding: '24px 0',
            background: colorBgContainer,
          }}
        >
          <Sider
            style={{
              background: colorBgContainer,
            }}
            width={200}
          >
            <Menu
              mode="inline"
              defaultSelectedKeys={['1']}
              defaultOpenKeys={['sub1']}
              style={{
                height: '100%',
              }}
              items={items2}
            />
          </Sider>
          <Content
            style={{
              padding: '0 24px',
              minHeight: 280,
            }}
          >
            Content
          </Content>
        </Layout>
      </Content>
      <Footer
        style={{
          textAlign: 'center',
        }}
      >
        大桔株式会社2023
      </Footer>
    </Layout>
    </BrowserRouter>
    </ContextProvider>
  );
};
export default App;
