        <p>规格：{size} {""}重量：{weight}g</p>
        <p >价格：¥{price}元</p>
        <button className='addToCart' onClick={()=>AddProduct(id)}>加入购物车{cartItem[id]}</button>


        </div>

    </div>
  )
}
