import React, { createContext, useState } from 'react'
import { Products } from '../products/Products';

export const proContext=createContext(null);
const defaultCart=()=>{
    let cart={};
    for(let i=1;i<Products.length+1;i++){
        cart[i]=0;
    }
    return cart;
}

export const  ContextProvider=(props)=> {
    const[cartItem,setCartItem]=useState(defaultCart())

    const AddProduct=(itemId)=>{
    setCartItem((prev)=>({...prev,[itemId]:prev[itemId]+1}))
    }
    const RemoveProduct=(itemId)=>{
        setCartItem((prev)=>({...prev,[itemId]:prev[itemId]-1}))
    }
    const InputProduct=(itemId,inputValue)=>{
      setCartItem((prev)=>({...prev,[itemId]:inputValue>0?inputValue:0}))
    }
    const contextValue = {cartItem,AddProduct,RemoveProduct,InputProduct}

  return (
    <proContext.Provider value= {contextValue}>
      {props.children}
      </proContext.Provider>
  )
}
